selector_to_html = {"a[href=\"../resources/glossary.html#term-Linking-key\"]": "<dt id=\"term-Linking-key\">Linking key</dt><dd><p>The linking key allows finding all accounts of a given account holder. This identifier is stored in encrypted form in the identity record of an account holder.</p></dd>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(`article.bd-article ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: true,
                placement: 'auto-start', maxWidth: 500, interactive: false,

            });
        };
    };
    console.log("tippy tips loaded!");
};
