selector_to_html = {"a[href=\"#ledger-downloads\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Ledger packages<a class=\"headerlink\" href=\"#ledger-packages\" title=\"Link to this heading\">#</a></h3><p>Once you have updated the firmware version, you can download the LEDGER app:</p>", "a[href=\"#update-reinstall-the-concordium-ledger-app-on-macos\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Update/reinstall the Concordium LEDGER app on macOS<a class=\"headerlink\" href=\"#update-reinstall-the-concordium-ledger-app-on-macos\" title=\"Link to this heading\">#</a></h3><p>For the app to work properly with the current version of the Desktop Wallet, make sure that you update to the latest version of the app.</p><p>When you update your LEDGER device, it should not be necessary to update the certificate.</p>", "a[href=\"#install-the-custom-certificate-on-ubuntu\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install the custom certificate on Ubuntu<a class=\"headerlink\" href=\"#install-the-custom-certificate-on-ubuntu\" title=\"Link to this heading\">#</a></h3><p>You now have to install a custom certificate to ensure that the LEDGER device trusts applications signed by Concordium\u2019s private key.</p>", "a[href=\"#install-the-custom-certificate-using-macos\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install the custom certificate using macOS<a class=\"headerlink\" href=\"#install-the-custom-certificate-using-macos\" title=\"Link to this heading\">#</a></h3><p>You now have to install a custom certificate to ensure that the LEDGER device trusts applications signed by Concordium\u2019s private key.</p>", "a[href=\"#update-app-ubuntu\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Update the Concordium LEDGER app on Ubuntu<a class=\"headerlink\" href=\"#update-the-concordium-ledger-app-on-ubuntu\" title=\"Link to this heading\">#</a></h3><p>For the app to work properly with the current version of the Desktop Wallet, make sure that you update to the latest version of the app.</p>", "a[href=\"#install-python-pip-macos\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install Homebrew, Python3, and pip<a class=\"headerlink\" href=\"#install-homebrew-python3-and-pip\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#set-up-the-ledger-device\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Set up the LEDGER device<a class=\"headerlink\" href=\"#set-up-the-ledger-device\" title=\"Link to this heading\">#</a></h2><p>The LEDGER device will generate the unique 24-word recovery phrase that is used to derive your private keys.</p>", "a[href=\"#install-python3-and-pip\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install Python3 and pip<a class=\"headerlink\" href=\"#install-python3-and-pip\" title=\"Link to this heading\">#</a></h3><p>Restart your computer, and then confirm that Python and Pip were installed.</p>", "a[href=\"#install-python-pip-windows\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install Python3 and pip<a class=\"headerlink\" href=\"#install-python3-and-pip\" title=\"Link to this heading\">#</a></h3><p>Restart your computer, and then confirm that Python and Pip were installed.</p>", "a[href=\"../installation/downloads.html#downloads\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Downloads<a class=\"headerlink\" href=\"#downloads\" title=\"Link to this heading\">#</a></h1><p>This topic contains information about where you can download the Concordium Wallets and tools for Mainnet and Testnet.</p>", "a[href=\"#update-the-concordium-ledger-app-on-ubuntu\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Update the Concordium LEDGER app on Ubuntu<a class=\"headerlink\" href=\"#update-the-concordium-ledger-app-on-ubuntu\" title=\"Link to this heading\">#</a></h3><p>For the app to work properly with the current version of the Desktop Wallet, make sure that you update to the latest version of the app.</p>", "a[href=\"#prerequisites\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Prerequisites<a class=\"headerlink\" href=\"#prerequisites\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#update-the-concordium-ledger-app-on-windows\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Update the Concordium LEDGER app on Windows<a class=\"headerlink\" href=\"#update-the-concordium-ledger-app-on-windows\" title=\"Link to this heading\">#</a></h3><p>For the app to work properly with the current version of the Desktop Wallet, make sure that you update to the latest version of the app.</p>", "a[href=\"#update-the-ledger-device-firmware\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Update the LEDGER device firmware<a class=\"headerlink\" href=\"#update-the-ledger-device-firmware\" title=\"Link to this heading\">#</a></h3><p>To find out which firmware version the LEDGER device is running, do the following:</p>", "a[href=\"#install-concordium-ledger-app-on-windows\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Install Concordium LEDGER app on Windows<a class=\"headerlink\" href=\"#install-concordium-ledger-app-on-windows\" title=\"Link to this heading\">#</a></h2><h3>Install Python3 and pip<a class=\"headerlink\" href=\"#install-python3-and-pip\" title=\"Link to this heading\">#</a></h3><p>Restart your computer, and then confirm that Python and Pip were installed.</p>", "a[href=\"#install-the-concordium-ledger-app-on-ubuntu\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install the Concordium LEDGER app on Ubuntu<a class=\"headerlink\" href=\"#install-the-concordium-ledger-app-on-ubuntu\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#install-the-concordium-ledger-app-on-macos\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install the Concordium LEDGER app on MacOS<a class=\"headerlink\" href=\"#install-the-concordium-ledger-app-on-macos\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#set-up-the-ledger-device-and-install-the-concordium-ledger-app\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Set up the LEDGER device and install the Concordium LEDGER App<a class=\"headerlink\" href=\"#set-up-the-ledger-device-and-install-the-concordium-ledger-app\" title=\"Link to this heading\">#</a></h1><p>To be able to sign and send transactions using the Desktop Wallet, you need a LEDGER hardware wallet, and you need to install the Concordium LEDGER Application on the LEDGER device. You can use Windows, macOS or Ubuntu to install the app.</p>", "a[href=\"#install-python3-and-pip-on-ubuntu\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install Python3 and pip on Ubuntu<a class=\"headerlink\" href=\"#install-python3-and-pip-on-ubuntu\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#install-concordium-ledger-app-on-ubuntu\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Install Concordium LEDGER app on Ubuntu<a class=\"headerlink\" href=\"#install-concordium-ledger-app-on-ubuntu\" title=\"Link to this heading\">#</a></h2><h3>Install Python3 and pip on Ubuntu<a class=\"headerlink\" href=\"#install-python3-and-pip-on-ubuntu\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#update-app-macos\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Update/reinstall the Concordium LEDGER app on macOS<a class=\"headerlink\" href=\"#update-reinstall-the-concordium-ledger-app-on-macos\" title=\"Link to this heading\">#</a></h3><p>For the app to work properly with the current version of the Desktop Wallet, make sure that you update to the latest version of the app.</p><p>When you update your LEDGER device, it should not be necessary to update the certificate.</p>", "a[href=\"#ledger-packages\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Ledger packages<a class=\"headerlink\" href=\"#ledger-packages\" title=\"Link to this heading\">#</a></h3><p>Once you have updated the firmware version, you can download the LEDGER app:</p>", "a[href=\"#update-app-windows\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Update the Concordium LEDGER app on Windows<a class=\"headerlink\" href=\"#update-the-concordium-ledger-app-on-windows\" title=\"Link to this heading\">#</a></h3><p>For the app to work properly with the current version of the Desktop Wallet, make sure that you update to the latest version of the app.</p>", "a[href=\"#install-concordium-ledger-app-on-macos\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Install Concordium LEDGER app on macOS<a class=\"headerlink\" href=\"#install-concordium-ledger-app-on-macos\" title=\"Link to this heading\">#</a></h2><h3>Install Homebrew, Python3, and pip<a class=\"headerlink\" href=\"#install-homebrew-python3-and-pip\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#install-homebrew-python3-and-pip\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install Homebrew, Python3, and pip<a class=\"headerlink\" href=\"#install-homebrew-python3-and-pip\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#install-the-custom-certificate-on-windows\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install the custom certificate on Windows<a class=\"headerlink\" href=\"#install-the-custom-certificate-on-windows\" title=\"Link to this heading\">#</a></h3><p>You now have to install a custom certificate on the LEDGER device to ensure that it trusts applications signed by Concordium\u2019s private key.</p>", "a[href=\"#install-the-concordium-ledger-app-on-windows\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Install the Concordium LEDGER app on Windows<a class=\"headerlink\" href=\"#install-the-concordium-ledger-app-on-windows\" title=\"Link to this heading\">#</a></h3>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(` ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: true,
                placement: 'auto-start', maxWidth: 500, interactive: false,

            });
        };
    };
    console.log("tippy tips loaded!");
};
